import React, { useState } from 'react'
import axios from 'axios';
import bcryptjs from 'bcryptjs';
import '../css/loginStyle.css';
import UserDetails from '../service/LoginService'

class LoginForm {
    constructor() {

        const [errorMessages, setErrorMessages] = useState({});
        const [isSubmitted, setIsSubmitted] = useState(false);
        
        const errors = {
            uname: "invalid username",
            pass: "invalid password"
        };
        const handleSubmit = (event) => {
            //Prevent page reload
            event.preventDefault();

            var { uname, pass } = document.forms[0];

            const userDetails = UserDetails.getDetails({uname});

            // Compare user info
            if (userDetails.data.userName == uname.value) {
                if (!bcryptjs.compareSync(pass,userDetails.data.password) ) {
                    // Invalid password
                    setErrorMessages({ name: "pass", message: errors.pass });
                } else {
                    setIsSubmitted(true);
                }
            } else {
                // Username not found
                setErrorMessages({ name: "uname", message: errors.uname });
            }
        };

        const renderErrorMessage = (name) => name === errorMessages.name && (
            <div className="error">{errorMessages.message}</div>
        );

        const renderForm = (
            <div>
                <form className='form' onSubmit={handleSubmit}>
                    <div className='register-Form'>

                        <h1 align="center">Login</h1>
                        <p align="center">Please enter username and password</p>
                        <hr></hr>
                        <h1>{this.userDetails}</h1>

                        <label><b>Username</b></label>
                        <input type="text" className='register' placeholder='username' name='uname'
                            onChange={e => this.username = e.target.value} />
                        {renderErrorMessage("uname")} 

                        {/* <label><b>Email</b></label>
                <input type="text" className='register' placeholder='abc.@' name='email'
                    onChange={e => this.email = e.target.value}/> */}

                        <label><b>Password</b></label>
                        <input type="password" className='register' placeholder='password' name='pass'
                            onChange={e => this.password = e.target.value} />
                        {renderErrorMessage("pass")}

                        {/* <label><b>Confirm Password</b></label>
                <input type="password" className='register' placeholder='password' name='confirmpassword'
                    onChange={e => this.confirmpassword = e.target.value}/> */}

                        <button className='regBtn'>Sign Up</button>
                    </div>
                </form>
            </div>
        );
        return (
            <div className="app">
                <div className="login-form">
                    <div className="title">Sign In</div>
                    {isSubmitted ? <div>User is successfully logged in</div> : renderForm}
                </div>
            </div>
        );

    }
}
export default LoginForm;
