import React, { Component } from 'react'
import axios from 'axios';
import bcryptjs from 'bcryptjs';
import {Navigate} from 'react-router-dom';

export default class LoginComponent extends Component {

    handleSubmit = e => {
        e.preventDefault();
        const data = {
            username : this.username,
            //email : this.email,
            password : this.password
            //confirmpassword : this.confirmpassword
        }
        console.log(data);
        const userDetails = axios.get(`http://localhost:9001/user/${this.username}`)
        //.then(
        //     res => { 
        //         if(bcryptjs.compareSync(this.password,res.data.password) ){
        //         return <Navigate to={'/products'}></Navigate>

        //         }
        //     }

        // ).catch(
        //     err => {
        //         console.log(err);
        //     }
        // )
        console.log(userDetails);
        
    };

  render() {
    return (
        <div>
            <form className='form' onSubmit={this.handleSubmit}>
                <div className='register-Form'>
                    <h1 align="center">Sign Up</h1>
                    <p align="center">Please fill in this form to create an account.</p>
                    <hr></hr>

                    <label><b>Username</b></label>
                    <input type="text" className='register' placeholder='username' name='username'
                        onChange={e => this.username = e.target.value}/>

                    {/* <label><b>Email</b></label>
                    <input type="text" className='register' placeholder='abc.@' name='email'
                        onChange={e => this.email = e.target.value}/> */}
                
                    <label><b>Password</b></label>
                    <input type="password" className='register' placeholder='password' name='password'
                        onChange={e => this.password = e.target.value}/>
                
                    {/* <label><b>Confirm Password</b></label>
                    <input type="password" className='register' placeholder='password' name='confirmpassword'
                        onChange={e => this.confirmpassword = e.target.value}/> */}
                    
                    <button className='regBtn'>Sign Up</button>
                </div>
            </form>
        </div>
    )
  }
}
