import axios from 'axios';
import { Component } from 'react';

const productList_url = "http://localhost:9001/user/";
class LoginService extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         userName : {}
      }
    }
    getDetails(username){
        var userDetails =  axios.get(productList_url+`${username}`);
        this.state.userName = userDetails;
        return userDetails;
    }
    getUserName(){
        return userDetails
    }
}

export default new LoginService();
