import React from 'react'

class RegisterForm extends React.Component {
    constructor() {
      super();
      this.state = {
        fields: {},
        errors: {}
      }

      this.handleChange = this.handleChange.bind(this);
      this.submituserRegistrationForm = this.submituserRegistrationForm.bind(this);

    };

    handleChange(e) {
      let fields = this.state.fields;
      fields[e.target.name] = e.target.value;
      this.setState({
        fields
      });

    }

    submituserRegistrationForm(e) {
      e.preventDefault();
      if (this.validateForm()) {
          let fields = {};
          fields["userName"] = "";
          fields["fullName"] = "";
          fields["email"] = "";
          fields["password"] = "";
          this.setState({fields:fields});
          alert("Form submitted");
      }

    }

    validateForm() {

      let fields = this.state.fields;
      let errors = {};
      let formIsValid = true;

      if (!fields["userName"]) {
        formIsValid = false;
        errors["userName"] = "*Please enter your userName.";
      }

      if (typeof fields["userName"] !== "undefined") {
        if (!fields["userName"].match(/^[a-zA-Z ]*$/)) {
          formIsValid = false;
          errors["userName"] = "*Please enter alphabet characters only.";
        }
      }

      if (!fields["email"]) {
        formIsValid = false;
        errors["email"] = "*Please enter your email-ID.";
      }

      if (typeof fields["email"] !== "undefined") {
        //regular expression for email validation
        var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
        if (!pattern.test(fields["email"])) {
          formIsValid = false;
          errors["email"] = "*Please enter valid email-ID.";
        }
      }

      if (!fields["mobileno"]) {
        formIsValid = false;
        errors["mobileno"] = "*Please enter your mobile no.";
      }

      if (typeof fields["mobileno"] !== "undefined") {
        if (!fields["mobileno"].match(/^[0-9]{10}$/)) {
          formIsValid = false;
          errors["mobileno"] = "*Please enter valid mobile no.";
        }
      }

      if (!fields["password"]) {
        formIsValid = false;
        errors["password"] = "*Please enter your password.";
      }

      if (typeof fields["password"] !== "undefined") {
        if (!fields["password"].match(/^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%&]).*$/)) {
          formIsValid = false;
          errors["password"] = "*Please enter secure and strong password.";
        }
      }

      this.setState({
        errors: errors
      });
      return formIsValid;


    }



  render() {
    return (
        <div>
            <form className='form' onSubmit={this.submituserRegistrationForm}>
                <div className='register-Form'>
                

                    <h1 align="center">Sign Up</h1>
                    <p align="center">Please enter userName and password</p>
                    <hr></hr>
                    

                    <label><b>UserName</b></label>
                    <input type="text" className='register' placeholder='userName' name='uname'/>
                    <div className="errorMsg">{this.state.errors.userName}</div>

                    <label><b>Full Name</b></label>
                    <input type="text" className='register' placeholder='fullName' name='fullName'/>
                    <div className="errorMsg">{this.state.errors.fullName}</div>

                    <label><b>Email</b></label>
                    <input type="text" className='register' placeholder='email' name='email'/>
                    <div className="errorMsg">{this.state.errors.email}</div>
                    {/* <label><b>Email</b></label>
                    <input type="text" className='register' placeholder='abc.@' name='email'
                        onChange={e => this.email = e.target.value}/> */}
                
                    <label><b>Password</b></label>
                    <input type="password" className='register' placeholder='password' name='pass'/>
                    <div className="errorMsg">{this.state.errors.password}</div>
                  
                    {/* <label><b>Confirm Password</b></label>
                    <input type="password" className='register' placeholder='password' name='confirmpassword'
                        onChange={e => this.confirmpassword = e.target.value}/> */}
                    
                    <button className='regBtn'>Sign in</button>
                    <button className='regBtn'>Sign up</button>
                </div>
            </form>
        </div>
    );
                    
}
}

export default RegisterForm;
